mapper.select:
    SELECT count(*) FROM weather_day


			areaName=#{areaName}


			areaCode=#{areaCode}


			and date = #{date}


			and year = #{year}


			and month = #{month}


			and day = #{day}


			and validStatus = #{validStatus}

select * from weather_day


		areaName=#{areaName}


		areaCode=#{areaCode}


		and date = #{date}


		and year = #{year}


		and month = #{month}


		and day = #{day}


		and validStatus = #{validStatus}



order by updateTime desc
limit #{offset},#{pageSize}
