mapper.select:
    SELECT count(*) FROM weather_month


			areaName=#{areaName}


			areaCode=#{areaCode}


			and yearMonth = #{yearMonth}


			and crawlFlag = #{crawlFlag}

			and validStatus = #{validStatus}

select * from weather_month


		areaName=#{areaName}


		areaCode=#{areaCode}


		and yearMonth = #{yearMonth}


		and crawlFlag = #{crawlFlag}

		and validStatus = #{validStatus}



order by updateTime desc
limit #{offset},#{pageSize}
